import{_ as e}from"./plugin-vue_export-helper-DlAUqK2U.js";import{c as a,f as l,o as n}from"./app-DtN_mEP7.js";const o={};function p(r,s){return n(),a("div",null,[...s[0]||(s[0]=[l(`<h2 id="数据库定义语言-ddl" tabindex="-1"><a class="header-anchor" href="#数据库定义语言-ddl"><span>数据库定义语言（DDL）</span></a></h2><h3 id="数据库操作" tabindex="-1"><a class="header-anchor" href="#数据库操作"><span>数据库操作</span></a></h3><p>我们可以通过<code>create database</code>来创建一个数据库：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">create</span><span style="color:#81A1C1;"> database</span><span style="color:#88C0D0;"> 数据库名</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>为了能够支持中文，我们在创建时可以设定编码格式：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">CREATE</span><span style="color:#81A1C1;"> DATABASE</span><span style="color:#88C0D0;"> IF</span><span style="color:#81A1C1;"> NOT</span><span style="color:#81A1C1;"> EXISTS</span><span style="color:#D8DEE9FF;"> 数据库名 </span><span style="color:#81A1C1;">DEFAULT</span><span style="color:#D8DEE9FF;"> CHARSET utf8 </span><span style="color:#81A1C1;">COLLATE</span><span style="color:#D8DEE9FF;"> utf8_general_ci;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>如果我们创建错误了，我们可以将此数据库删除，通过使用<code>drop database</code>来删除一个数据库：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">drop</span><span style="color:#81A1C1;"> database</span><span style="color:#D8DEE9FF;"> 数据库名</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h3 id="创建表" tabindex="-1"><a class="header-anchor" href="#创建表"><span>创建表</span></a></h3><p>数据库创建完成后，我们一般通过<code>create table</code>语句来创建一张表：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">create</span><span style="color:#81A1C1;"> table</span><span style="color:#88C0D0;"> 表名</span><span style="color:#D8DEE9FF;">(列名 数据类型[列级约束条件],</span></span>
<span class="line"><span style="color:#D8DEE9FF;">             列名 数据类型[列级约束条件],</span></span>
<span class="line"><span style="color:#D8DEE9FF;">             ...</span></span>
<span class="line"><span style="color:#D8DEE9FF;">             [,表级约束条件])</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="sql数据类型" tabindex="-1"><a class="header-anchor" href="#sql数据类型"><span>SQL数据类型</span></a></h3><p>以下的数据类型用于字符串存储：</p><ul><li>char(n)可以存储任意字符串，但是是固定长度为n，如果插入的长度小于定义长度时，则用空格填充。</li><li>varchar(n)也可以存储任意数量字符串，长度不固定，但不能超过n，不会用空格填充。</li></ul><p>以下数据类型用于存储数字：</p><ul><li>smallint用于存储小的整数，范围在 (-32768，32767)</li><li>int用于存储一般的整数，范围在 (-2147483648，2147483647)</li><li>bigint用于存储大型整数，范围在 (-9,223,372,036,854,775,808，9,223,372,036,854,775,807)</li><li>float用于存储单精度小数</li><li>double用于存储双精度的小数</li></ul><p>以下数据类型用于存储时间：</p><ul><li>date存储日期</li><li>time存储时间</li><li>year存储年份</li><li>datetime用于混合存储日期+时间</li></ul><h3 id="列级约束条件" tabindex="-1"><a class="header-anchor" href="#列级约束条件"><span>列级约束条件</span></a></h3><p>列级约束有六种：主键Primary key、外键foreign key 、唯一 unique、检查 check （MySQL不支持）、默认default 、非空/空值 not null/ null</p><h3 id="表级约束条件" tabindex="-1"><a class="header-anchor" href="#表级约束条件"><span>表级约束条件</span></a></h3><p>表级约束有四种：主键、外键、唯一、检查</p><p>现在我们通过SQL语句来创建我们之前提到的三张表。</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#D8DEE9FF;">[CONSTRAINT &lt;外键名&gt;] </span><span style="color:#81A1C1;">FOREIGN KEY</span><span style="color:#D8DEE9FF;"> 字段名 [，字段名2，…] </span><span style="color:#81A1C1;">REFERENCES</span><span style="color:#81A1C1;"> &lt;</span><span style="color:#D8DEE9FF;">主表名</span><span style="color:#81A1C1;">&gt;</span><span style="color:#D8DEE9FF;"> 主键列1 [，主键列2，…]</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h3 id="修改表" tabindex="-1"><a class="header-anchor" href="#修改表"><span>修改表</span></a></h3><p>如果我们想修改表结构，我们可以通过<code>alter table</code>来进行修改：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">ALTER</span><span style="color:#81A1C1;"> TABLE</span><span style="color:#D8DEE9FF;"> 表名[ADD 新列名 数据类型[列级约束条件]]</span></span>
<span class="line"><span style="color:#D8DEE9FF;">							 [DROP COLUMN 列名[restrict|cascade]]</span></span>
<span class="line"><span style="color:#D8DEE9FF;">							 [ALTER COLUMN 列名 新数据类型]</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>我们可以通过ADD来添加一个新的列，通过DROP来删除一个列，不过我们可以添加restrict或cascade，默认是restrict，表示如果此列作为其他表的约束或视图引用到此列时，将无法删除，而cascade会强制连带引用此列的约束、视图一起删除。还可以通过ALTER来修改此列的属性。</p><h3 id="删除表" tabindex="-1"><a class="header-anchor" href="#删除表"><span>删除表</span></a></h3><p>我们可以通过<code>drop table</code>来删除一个表：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">DROP</span><span style="color:#81A1C1;"> TABLE</span><span style="color:#D8DEE9FF;"> 表名[restrict|cascade]</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>其中restrict和cascade上面的效果一致。</p><hr><h2 id="数据库操纵语言-dml" tabindex="-1"><a class="header-anchor" href="#数据库操纵语言-dml"><span>数据库操纵语言（DML）</span></a></h2><p>前面我们已经学习了如何使用SQL语句来创建、修改、删除数据库以及表，而如何向数据库中插入、删除、更新数据，将是本版块讨论的重点。</p><h3 id="插入数据" tabindex="-1"><a class="header-anchor" href="#插入数据"><span>插入数据</span></a></h3><p>通过使用<code>insert into</code>语句来向数据库中插入一条数据（一条记录）：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">INSERT INTO</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">VALUES</span><span style="color:#D8DEE9FF;">(值1, 值2, 值3)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>如果插入的数据与列一一对应，那么可以省略列名，但是如果希望向指定列上插入数据，就需要给出列名：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">INSERT INTO</span><span style="color:#D8DEE9FF;"> 表名(列名1, 列名2) </span><span style="color:#81A1C1;">VALUES</span><span style="color:#D8DEE9FF;">(值1, 值2)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们也可以一次性向数据库中插入多条数据：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">INSERT INTO</span><span style="color:#D8DEE9FF;"> 表名(列名1, 列名2) </span><span style="color:#81A1C1;">VALUES</span><span style="color:#D8DEE9FF;">(值1, 值2), (值1, 值2), (值1, 值2)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们来试试看向我们刚刚创建的表中添加三条数据。</p><h3 id="修改数据" tabindex="-1"><a class="header-anchor" href="#修改数据"><span>修改数据</span></a></h3><p>我们可以通过<code>update</code>语句来更新表中的数据：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">UPDATE</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">SET</span><span style="color:#D8DEE9FF;"> 列名</span><span style="color:#81A1C1;">=</span><span style="color:#D8DEE9FF;">值,... </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>注意，SQL语句中的等于判断是<code>=</code></p><p>**警告：**如果忘记添加<code>WHERE</code>字句来限定条件，将使得整个表中此列的所有数据都被修改！</p><h3 id="删除数据" tabindex="-1"><a class="header-anchor" href="#删除数据"><span>删除数据</span></a></h3><p>我们可以通过使用<code>delete</code>来删除表中的数据：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">DELETE</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>通过这种方式，将删除表中全部数据，我们也可以使用<code>where</code>来添加条件，只删除指定的数据：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">DELETE</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><hr><h2 id="数据库查询语言-dql" tabindex="-1"><a class="header-anchor" href="#数据库查询语言-dql"><span>数据库查询语言（DQL）</span></a></h2><p>数据库的查询是我们整个数据库学习中的重点内容，面对数据库中庞大的数据，该如何去寻找我们想要的数据，就是我们主要讨论的问题。</p><h3 id="单表查询" tabindex="-1"><a class="header-anchor" href="#单表查询"><span>单表查询</span></a></h3><p>单表查询是最简单的一种查询，我们只需要在一张表中去查找数据即可，通过使用<code>select</code>语句来进行单表查询：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#616E88;">-- 指定查询某一列数据</span></span>
<span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> 列名[,列名] </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名</span></span>
<span class="line"><span style="color:#616E88;">-- 会以别名显示此列</span></span>
<span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> 列名 别名 </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名</span></span>
<span class="line"><span style="color:#616E88;">-- 查询所有的列数据</span></span>
<span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名</span></span>
<span class="line"><span style="color:#616E88;">-- 只查询不重复的值</span></span>
<span class="line"><span style="color:#81A1C1;">SELECT DISTINCT</span><span style="color:#D8DEE9FF;"> 列名 </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>我们也可以添加<code>where</code>字句来限定查询目标：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h3 id="常用查询条件" tabindex="-1"><a class="header-anchor" href="#常用查询条件"><span>常用查询条件</span></a></h3><ul><li>一般的比较运算符，包括=、&gt;、&lt;、&gt;=、&lt;=、!=等。</li><li>是否在集合中：in、not in</li><li>字符模糊匹配：like，not like</li><li>多重条件连接查询：and、or、not</li></ul><p>我们来尝试使用一下上面这几种条件。</p><h3 id="排序查询" tabindex="-1"><a class="header-anchor" href="#排序查询"><span>排序查询</span></a></h3><p>我们可以通过<code>order by</code>来将查询结果进行排序：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件 </span><span style="color:#81A1C1;">ORDER BY</span><span style="color:#D8DEE9FF;"> 列名 </span><span style="color:#81A1C1;">ASC</span><span style="color:#D8DEE9FF;">|</span><span style="color:#81A1C1;">DESC</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>使用ASC表示升序排序，使用DESC表示降序排序，默认为升序。</p><p>我们也可以可以同时添加多个排序：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件 </span><span style="color:#81A1C1;">ORDER BY</span><span style="color:#D8DEE9FF;"> 列名1 </span><span style="color:#81A1C1;">ASC</span><span style="color:#D8DEE9FF;">|</span><span style="color:#81A1C1;">DESC</span><span style="color:#D8DEE9FF;">, 列名2 </span><span style="color:#81A1C1;">ASC</span><span style="color:#D8DEE9FF;">|</span><span style="color:#81A1C1;">DESC</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>这样会先按照列名1进行排序，每组列名1相同的数据再按照列名2排序。</p><h3 id="聚集函数" tabindex="-1"><a class="header-anchor" href="#聚集函数"><span>聚集函数</span></a></h3><p>聚集函数一般用作统计，包括：</p><ul><li><code>count([distinct]*)</code>统计所有的行数（distinct表示去重再统计，下同）</li><li><code>count([distinct]列名)</code>统计某列的值总和</li><li><code>sum([distinct]列名)</code>求一列的和（注意必须是数字类型的）</li><li><code>avg([distinct]列名)</code>求一列的平均值（注意必须是数字类型）</li><li><code>max([distinct]列名)</code>求一列的最大值</li><li><code>min([distinct]列名)</code>求一列的最小值</li></ul><p>一般聚集函数是这样使用的：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> count</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">distinct</span><span style="color:#D8DEE9FF;"> 列名) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h3 id="分组和分页查询" tabindex="-1"><a class="header-anchor" href="#分组和分页查询"><span>分组和分页查询</span></a></h3><p>通过使用<code>group by</code>来对查询结果进行分组，它需要结合聚合函数一起使用：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> sum</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">*</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件 </span><span style="color:#81A1C1;">GROUP BY</span><span style="color:#D8DEE9FF;"> 列名</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们还可以添加<code>having</code>来限制分组条件：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> sum</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">*</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件 </span><span style="color:#81A1C1;">GROUP BY</span><span style="color:#D8DEE9FF;"> 列名 </span><span style="color:#81A1C1;">HAVING</span><span style="color:#D8DEE9FF;"> 约束条件</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们可以通过<code>limit</code>来限制查询的数量，只取前n个结果：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">LIMIT</span><span style="color:#D8DEE9FF;"> 数量</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们也可以进行分页：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">LIMIT</span><span style="color:#D8DEE9FF;"> 起始位置,数量</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h3 id="多表查询" tabindex="-1"><a class="header-anchor" href="#多表查询"><span>多表查询</span></a></h3><p>多表查询是同时查询的两个或两个以上的表，多表查询会提通过连接转换为单表查询。</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表1, 表2</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>直接这样查询会得到两张表的笛卡尔积，也就是每一项数据和另一张表的每一项数据都结合一次，会产生庞大的数据。</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表1, 表2 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>这样，只会从笛卡尔积的结果中得到满足条件的数据。</p><p>**注意：**如果两个表中都带有此属性吗，需要添加表名前缀来指明是哪一个表的数据。</p><h3 id="自身连接查询" tabindex="-1"><a class="header-anchor" href="#自身连接查询"><span>自身连接查询</span></a></h3><p>自身连接，就是将表本身和表进行笛卡尔积计算，得到结果，但是由于表名相同，因此要先起一个别名：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 别名1, 表名 别名2</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>其实自身连接查询和前面的是一样的，只是连接对象变成自己和自己了。</p><h3 id="外连接查询" tabindex="-1"><a class="header-anchor" href="#外连接查询"><span>外连接查询</span></a></h3><p>外连接就是专门用于联合查询情景的，比如现在有一个存储所有用户的表，还有一张用户详细信息的表，我希望将这两张表结合到一起来查看完整的数据，我们就可以通过使用外连接来进行查询，外连接有三种方式：</p><ul><li>通过使用<code>inner join</code>进行内连接，只会返回两个表满足条件的交集部分：</li></ul><figure><img src="https://img-blog.csdnimg.cn/2019053022120536.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3dlaXhpbl80Mzg1ODIwMQ==,size_16,color_FFFFFF,t_70" alt="在这里插入图片描述" tabindex="0" loading="lazy"><figcaption>在这里插入图片描述</figcaption></figure><ul><li>通过使用<code>left join</code>进行左连接，不仅会返回两个表满足条件的交集部分，也会返回左边表中的全部数据，而在右表中缺失的数据会使用<code>null</code>来代替（右连接<code>right join</code>同理，只是反过来而已，这里就不再介绍了）：</li></ul><figure><img src="https://img-blog.csdnimg.cn/20190530221543230.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3dlaXhpbl80Mzg1ODIwMQ==,size_16,color_FFFFFF,t_70" alt="在这里插入图片描述" tabindex="0" loading="lazy"><figcaption>在这里插入图片描述</figcaption></figure><h3 id="嵌套查询" tabindex="-1"><a class="header-anchor" href="#嵌套查询"><span>嵌套查询</span></a></h3><p>我们可以将查询的结果作为另一个查询的条件，比如：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> *</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 列名 </span><span style="color:#81A1C1;">=</span><span style="color:#D8DEE9FF;"> (</span><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> 列名 </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> 表名 </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> 条件)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们来再次尝试编写一下在最开始我们查找某教师所有学生的SQL语句。</p><hr><h2 id="数据库控制语言-dcl" tabindex="-1"><a class="header-anchor" href="#数据库控制语言-dcl"><span>数据库控制语言（DCL）</span></a></h2><p>庞大的数据库不可能由一个人来管理，我们需要更多的用户来一起管理整个数据库。</p><h3 id="创建用户" tabindex="-1"><a class="header-anchor" href="#创建用户"><span>创建用户</span></a></h3><p>我们可以通过<code>create user</code>来创建用户：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">CREATE</span><span style="color:#81A1C1;"> USER</span><span style="color:#88C0D0;"> 用户名</span><span style="color:#D8DEE9FF;"> identified </span><span style="color:#81A1C1;">by</span><span style="color:#D8DEE9FF;"> 密码;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>也可以不带密码：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">CREATE</span><span style="color:#81A1C1;"> USER</span><span style="color:#88C0D0;"> 用户名</span><span style="color:#D8DEE9FF;">;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们可以通过@来限制用户登录的登录IP地址，<code>%</code>表示匹配所有的IP地址，默认使用的就是任意IP地址。</p><h3 id="登陆用户" tabindex="-1"><a class="header-anchor" href="#登陆用户"><span>登陆用户</span></a></h3><p>首先需要添加一个环境变量，然后我们通过cmd去登陆mysql：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">login</span><span style="color:#81A1C1;"> -</span><span style="color:#D8DEE9FF;">u 用户名 </span><span style="color:#81A1C1;">-</span><span style="color:#D8DEE9FF;">p</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>输入密码后即可登陆此用户，我们输入以下命令来看看能否访问所有数据库：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#D8DEE9FF;">show databases;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们发现，虽然此用户能够成功登录，但是并不能查看完整的数据库列表，这是因为此用户还没有权限！</p><h3 id="用户授权" tabindex="-1"><a class="header-anchor" href="#用户授权"><span>用户授权</span></a></h3><p>我们可以通过使用<code>grant</code>来为一个数据库用户进行授权：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">grant</span><span style="color:#D8DEE9FF;"> all|权限1,权限2...(列1,...) </span><span style="color:#81A1C1;">on</span><span style="color:#D8DEE9FF;"> 数据库.表 </span><span style="color:#81A1C1;">to</span><span style="color:#D8DEE9FF;"> 用户 [with grant option]</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>其中all代表授予所有权限，当数据库和表为<code>*</code>，代表为所有的数据库和表都授权。如果在最后添加了<code>with grant option</code>，那么被授权的用户还能将已获得的授权继续授权给其他用户。</p><p>我们可以使用<code>revoke</code>来收回一个权限：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">revoke</span><span style="color:#D8DEE9FF;"> all|权限1,权限2...(列1,...) </span><span style="color:#81A1C1;">on</span><span style="color:#D8DEE9FF;"> 数据库.表 </span><span style="color:#81A1C1;">from</span><span style="color:#D8DEE9FF;"> 用户</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><hr><h2 id="视图" tabindex="-1"><a class="header-anchor" href="#视图"><span>视图</span></a></h2><p>视图本质就是一个查询的结果，不过我们每次都可以通过打开视图来按照我们想要的样子查看数据。既然视图本质就是一个查询的结果，那么它本身就是一个虚表，并不是真实存在的，数据实际上还是存放在原来的表中。</p><p>我们可以通过<code>create view</code>来创建视图;</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">CREATE</span><span style="color:#81A1C1;"> VIEW</span><span style="color:#88C0D0;"> 视图名称</span><span style="color:#D8DEE9FF;">(列名) </span><span style="color:#81A1C1;">as</span><span style="color:#D8DEE9FF;"> 子查询语句 [WITH CHECK OPTION];</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>WITH CHECK OPTION是指当创建后，如果更新视图中的数据，是否要满足子查询中的条件表达式，不满足将无法插入，创建后，我们就可以使用<code>select</code>语句来直接查询视图上的数据了，因此，还能在视图的基础上，导出其他的视图。</p><ol><li>若视图是由两个以上基本表导出的，则此视图不允许更新。</li><li>若视图的字段来自字段表达式或常数，则不允许对此视图执行INSERT和UPDATE操作，但允许执行DELETE操作。</li><li>若视图的字段来自集函数，则此视图不允许更新。</li><li>若视图定义中含有GROUP BY子句，则此视图不允许更新。</li><li>若视图定义中含有DISTINCT短语，则此视图不允许更新。</li><li>若视图定义中有嵌套查询，并且内层查询的FROM子句中涉及的表也是导出该视图的基本表，则此视图不允许更新。例如将成绩在平均成绩之上的元组定义成一个视图GOOD_SC： CREATE VIEW GOOD_SC AS SELECT Sno, Cno, Grade FROM SC WHERE Grade &gt; (SELECT AVG(Grade) FROM SC); 　　导出视图GOOD_SC的基本表是SC，内层查询中涉及的表也是SC，所以视图GOOD_SC是不允许更新的。</li><li>一个不允许更新的视图上定义的视图也不允许更新</li></ol><p>通过<code>drop</code>来删除一个视图：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">drop</span><span style="color:#81A1C1;"> view</span><span style="color:#D8DEE9FF;"> apptest</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><hr><h2 id="索引" tabindex="-1"><a class="header-anchor" href="#索引"><span>索引</span></a></h2><p>在数据量变得非常庞大时，通过创建索引，能够大大提高我们的查询效率，就像Hash表一样，它能够快速地定位元素存放的位置，我们可以通过下面的命令创建索引：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#616E88;">-- 创建索引</span></span>
<span class="line"><span style="color:#81A1C1;">CREATE</span><span style="color:#81A1C1;"> INDEX</span><span style="color:#88C0D0;"> 索引名称</span><span style="color:#81A1C1;"> ON</span><span style="color:#D8DEE9FF;"> 表名 (列名)</span></span>
<span class="line"><span style="color:#616E88;">-- 查看表中的索引</span></span>
<span class="line"><span style="color:#D8DEE9FF;">show </span><span style="color:#81A1C1;">INDEX</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>我们也可以通过下面的命令删除一个索引：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">drop</span><span style="color:#81A1C1;"> index</span><span style="color:#D8DEE9FF;"> 索引名称 </span><span style="color:#81A1C1;">on</span><span style="color:#D8DEE9FF;"> 表名</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>虽然添加索引后会使得查询效率更高，但是我们不能过度使用索引，索引为我们带来高速查询效率的同时，也会在数据更新时产生额外建立索引的开销，同时也会占用磁盘资源。</p><hr><h2 id="触发器" tabindex="-1"><a class="header-anchor" href="#触发器"><span>触发器</span></a></h2><p>触发器就像其名字一样，在某种条件下会自动触发，在<code>select</code>/<code>update</code>/<code>delete</code>时，会自动执行我们预先设定的内容，触发器通常用于检查内容的安全性，相比直接添加约束，触发器显得更加灵活。</p><p>触发器所依附的表称为基本表，当触发器表上发生<code>select</code>/<code>update</code>/<code>delete</code>等操作时，会自动生成两个临时的表（new表和old表，只能由触发器使用）</p><p>比如在<code>insert</code>操作时，新的内容会被插入到new表中；在<code>delete</code>操作时，旧的内容会被移到old表中，我们仍可在old表中拿到被删除的数据；在<code>update</code>操作时，旧的内容会被移到old表中，新的内容会出现在new表中。</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">CREATE</span><span style="color:#81A1C1;"> TRIGGER</span><span style="color:#88C0D0;"> 触发器名称</span><span style="color:#D8DEE9FF;"> [BEFORE|AFTER] [INSERT|UPDATE|DELETE] </span><span style="color:#81A1C1;">ON</span><span style="color:#D8DEE9FF;"> 表名</span><span style="color:#81A1C1;">/</span><span style="color:#D8DEE9FF;">视图名 </span><span style="color:#81A1C1;">FOR</span><span style="color:#D8DEE9FF;"> EACH </span><span style="color:#81A1C1;">ROW</span><span style="color:#81A1C1;"> DELETE</span><span style="color:#81A1C1;"> FROM</span><span style="color:#D8DEE9FF;"> student </span><span style="color:#81A1C1;">WHERE</span><span style="color:#D8DEE9FF;"> student.sno </span><span style="color:#81A1C1;">=</span><span style="color:#D8DEE9FF;"> new.sno</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>FOR EACH ROW表示针对每一行都会生效，无论哪行进行指定操作都会执行触发器！</p><p>通过下面的命令来查看触发器：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#D8DEE9FF;">SHOW TRIGGERS</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>如果不需要，我们就可以删除此触发器：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">DROP</span><span style="color:#81A1C1;"> TRIGGER</span><span style="color:#D8DEE9FF;"> 触发器名称</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><hr><h2 id="事务" tabindex="-1"><a class="header-anchor" href="#事务"><span>事务</span></a></h2><p>当我们要进行的操作非常多时，比如要依次删除很多个表的数据，我们就需要执行大量的SQL语句来完成，这些数据库操作语句就可以构成一个事务！只有Innodb引擎支持事务，我们可以这样来查看支持的引擎：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#D8DEE9FF;">SHOW ENGINES;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>MySQL默认采用的是Innodb引擎，我们也可以去修改为其他的引擎。</p><p>事务具有以下特性：</p><ul><li>**原子性：**一个事务（transaction）中的所有操作，要么全部完成，要么全部不完成，不会结束在中间某个环节。事务在执行过程中发生错误，会被回滚（Rollback）到事务开始前的状态，就像这个事务从来没有执行过一样。</li><li>**一致性：**在事务开始之前和事务结束以后，数据库的完整性没有被破坏。这表示写入的资料必须完全符合所有的预设规则，这包含资料的精确度、串联性以及后续数据库可以自发性地完成预定的工作。</li><li>**隔离性：**数据库允许多个并发事务同时对其数据进行读写和修改的能力，隔离性可以防止多个事务并发执行时由于交叉执行而导致数据的不一致。事务隔离分为不同级别，包括读未提交（Read uncommitted）、读提交（read committed）、可重复读（repeatable read）和串行化（Serializable）。</li><li>**持久性：**事务处理结束后，对数据的修改就是永久的，即便系统故障也不会丢失。</li></ul><p>我们通过以下例子来探究以下事务：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">begin</span><span style="color:#D8DEE9FF;">;   #开始事务</span></span>
<span class="line"><span style="color:#D8DEE9FF;">...</span></span>
<span class="line"><span style="color:#81A1C1;">rollback</span><span style="color:#D8DEE9FF;">;  #回滚事务</span></span>
<span class="line"><span style="color:#D8DEE9FF;">savepoint 回滚点;  #添加回滚点</span></span>
<span class="line"><span style="color:#81A1C1;">rollback</span><span style="color:#81A1C1;"> to</span><span style="color:#D8DEE9FF;"> 回滚点; #回滚到指定回滚点</span></span>
<span class="line"><span style="color:#D8DEE9FF;">...</span></span>
<span class="line"><span style="color:#81A1C1;">commit</span><span style="color:#D8DEE9FF;">; #提交事务</span></span>
<span class="line"><span style="color:#616E88;">-- 一旦提交，就无法再进行回滚了！</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><hr><h2 id="函数" tabindex="-1"><a class="header-anchor" href="#函数"><span>函数</span></a></h2><p>其实函数我们在之前已经接触到一部分了，在JavaWeb阶段，我们了解了聚集函数，聚集函数一般用作统计，包括：</p><ul><li>count([distinct]*) 统计所有的行数（distinct表示去重再统计，下同）</li><li>count([distinct]列名) 统计某列的值总和</li><li>sum([distinct]列名) 求一列的和（注意必须是数字类型的）</li><li>avg([distinct]列名) 求一列的平均值（注意必须是数字类型）</li><li>max([distinct]列名) 求一列的最大值</li><li>min([distinct]列名) 求一列的最小值</li></ul><p>比如我们需要计算某个表一共有多少行：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> COUNT</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">*</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>通过使用COUNT聚集函数，就可以快速统计并得到结果，比如我们想计算某一列上所有数字的和：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> SUM</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">sid</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>通过SUM聚集函数，就可以快速计算每一列的和，实际上这些函数都是由系统提供的函数，我们可以直接使用。</p><p>本版块我们会详细介绍各类系统函数以及如何编写自定义函数。</p><h3 id="系统函数" tabindex="-1"><a class="header-anchor" href="#系统函数"><span>系统函数</span></a></h3><p>系统为我们提供的函数也是非常实用的，我们将会分为几个类型进行讲解。</p><h4 id="字符串函数" tabindex="-1"><a class="header-anchor" href="#字符串函数"><span>字符串函数</span></a></h4><p>处理字符串是一个比较重要的内容，我们可以使用字符串函数来帮助我们快速处理字符串，其中常用比如用于字符串分割的函数有：</p><ul><li>substring(字符串, 起始位置, 结束位置) 同Java中String类的substring一致，但是注意下标是从1开始，下同</li><li>left(字符串, 长度) 从最左边向右截取字符串</li><li>right(字符串, 长度) 从最右边向左截取字符串</li></ul><p>比如我们只想获取所有学生姓名的第二个字，那么可以像这样写：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> SUBSTRING</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">name</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">2</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">2</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>比如我们想获取所有学生姓名的第一个字，可以像这样写：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> LEFT</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">name</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">1</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们还可以利用字符串函数来快速将所有的字母转换为大写字母或是快速转换为小写字母：</p><ul><li>upper(字符串) 字符串中的所有字母转换为大写字母</li><li>lower(字符串) 字符串中的所有字母转换为小写字母</li></ul><p>比如我们希望将一个字符串所有字符专为大写：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> UPPER</span><span style="color:#D8DEE9FF;">(</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">abcdefg</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们也可以像Java中那样直接对字符串中的内容进行替换：</p><ul><li>replace(字符串, 原文, 替换文) 同Java中String的replace效果</li></ul><p>比如现在我们希望将查询到的所有同学的名称中的<code>小</code>全部替换<code>大</code>：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> REPLACE</span><span style="color:#D8DEE9FF;">(</span><span style="color:#ECEFF4;">\`</span><span style="color:#A3BE8C;">name</span><span style="color:#ECEFF4;">\`</span><span style="color:#D8DEE9FF;">, </span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">小</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">, </span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">大</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>字符串也支持进行拼接，系统提供了字符串的拼接函数：</p><ul><li>concat(字符串1, 字符串2) 连接两个字符串</li></ul><p>比如我们希望将查询到的所有同学的名称最后都添加一个<code>子</code>字：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> concat</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">name</span><span style="color:#D8DEE9FF;">, </span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">子</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>最后就是计算字符串的长度：</p><ul><li>length(字符串) 获取字符串长度（注意如果使用的是UTF-8编码格式，那么一个汉字占3字节，数字和字母占一个字节）</li></ul><p>比如我们要获取所有人名字的长度：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> LENGTH</span><span style="color:#D8DEE9FF;">(</span><span style="color:#ECEFF4;">\`</span><span style="color:#A3BE8C;">name</span><span style="color:#ECEFF4;">\`</span><span style="color:#D8DEE9FF;">) </span><span style="color:#81A1C1;">FROM</span><span style="color:#D8DEE9FF;"> student</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h4 id="日期函数" tabindex="-1"><a class="header-anchor" href="#日期函数"><span>日期函数</span></a></h4><p>MySQL提供的日期函数也非常实用，我们可以快速对日期进行操作，比如我们想要快速将日期添加N天，就可以使用：</p><ul><li>date_add(日期, interval 增量 单位)</li></ul><p>比如我们希望让2022-1-1向后5天：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> DATE_ADD(</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">2022-1-1</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">,INTERVAL </span><span style="color:#B48EAD;">5</span><span style="color:#81A1C1;"> day</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>同理，向前1年：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> DATE_ADD(</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">2022-1-1</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">,INTERVAL </span><span style="color:#81A1C1;">-</span><span style="color:#B48EAD;">1</span><span style="color:#81A1C1;"> year</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>单位有：year（年）、month（月）、day（日）、hour（小时）、minute（分钟）、second（秒）</p><p>我们还可以快速计算日期的间隔天数：</p><ul><li>datediff(日期1, 日期2)</li></ul><p>比如我们想计算2022年的2月有多少天：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> DATEDIFF</span><span style="color:#D8DEE9FF;">(</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">2022-3-1</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">,</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">2022-2-1</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>如果我们想快速获取当前时间的话，可以使用这些：</p><ul><li>curdate() 当前的日期</li><li>curtime() 当前的时间</li><li>now() 当前的日期+时间</li></ul><p>此函数之前我们在编写实战项目的时候已经使用过了，这里就不演示了。我们也可以单独获取时间中的某个值：</p><ul><li>day(日期) 获取日期是几号</li><li>month(日期) 获取日期是几月</li><li>year(日期) 获取日期是哪一年</li></ul><p>比如我们想获取今天是几号：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> DAY</span><span style="color:#D8DEE9FF;">(</span><span style="color:#81A1C1;">NOW</span><span style="color:#ECEFF4;">()</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><h4 id="数学函数" tabindex="-1"><a class="header-anchor" href="#数学函数"><span>数学函数</span></a></h4><p>数学函数比较常规，基本与Java的Math工具类一致，这里列出即可，各位可以自行尝试：</p><ul><li>abs(x) 求x的绝对值</li><li>ceiling(x) x向上取整</li><li>floor(x) x向下取整</li><li>round(x, 精度) x取四舍五入，遵循小数点精度</li><li>exp(x) e的x次方</li><li>rand() 0-1之间的随机数</li><li>log(x) x的对数</li><li>pi() π</li><li>power(x, n) x的n次方</li><li>sqrt(x) x的平方根</li><li>sin(x) cos(x) tan(x) 三角函数（貌似没有arctan这类反函数？）</li></ul><h4 id="类型转换函数" tabindex="-1"><a class="header-anchor" href="#类型转换函数"><span>类型转换函数</span></a></h4><p>MySQL的类型转换也分为隐式类型转换和显示类型转换，首先我们来看看隐式类型转换：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#B48EAD;"> 1</span><span style="color:#81A1C1;">+</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">2</span><span style="color:#ECEFF4;">&#39;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>虽然这句中既包含了数字和字符，但是字符串会被进行隐式转换（注意这里并不是按照字符的ASCII码转换，而是写的多少表示多少）所以最后得到的就是1+2的结果为3</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> CONCAT</span><span style="color:#D8DEE9FF;">(</span><span style="color:#B48EAD;">1</span><span style="color:#D8DEE9FF;">, </span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">2</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>这里因为需要传入字符串类型的数据，但是我们给的是1这个数字，因此这里也会发生隐式类型转换，1会被直接转换为字符串的&#39;1&#39;，所以这里得到的结果是&#39;12&#39;</p><p>在某些情况下，我们可能需要使用强制类型转换来将得到的数据转换成我们需要的数据类型，这时就需要用到类型转换函数了，MySQL提供了：</p><ul><li>cast(数据 as 数据类型)</li></ul><p>数据类型有以下几种：</p><ul><li>BINARY[(N)] ：二进制字符串，转换后长度小于N个字节</li><li>CHAR[(N)] ：字符串，转换后长度小于N个字符</li><li>DATE ：日期</li><li>DATETIME ：日期时间</li><li>DECIMAL[(M[,N])] ：浮点数，M为数字总位数(包括整数部分和小数部分)，N为小数点后的位数</li><li>SIGNED [INTEGER] ：有符号整数</li><li>TIME ：时间</li><li>UNSIGNED [INTEGER] ：无符号整数</li></ul><p>比如我们现在需要将一个浮点数转换为一个整数：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> CAST</span><span style="color:#D8DEE9FF;">(</span><span style="color:#88C0D0;">pi</span><span style="color:#D8DEE9FF;">() </span><span style="color:#81A1C1;">AS</span><span style="color:#D8DEE9FF;"> SIGNED)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>我们还可以将字符串转换为数字，会自动进行扫描，值得注意的是一旦遇到非数字的字符，会停止扫描：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#88C0D0;"> CAST</span><span style="color:#D8DEE9FF;">(</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">123abc456</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#81A1C1;"> as</span><span style="color:#D8DEE9FF;"> SIGNED)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>除了cast以外还有convert函数，功能比较相似，这里就不做讲解了。</p><h4 id="流程控制函数" tabindex="-1"><a class="header-anchor" href="#流程控制函数"><span>流程控制函数</span></a></h4><p>MySQL还为我们提供了很多的逻辑判断函数，比如：</p><ul><li>if(条件表达式, 结果1, 结果2) 与Java中的三目运算符一致 a &gt; b ? &quot;AAA&quot; : &quot;BBB&quot;</li><li>ifnull(值1, 值2) 如果值1为NULL则返回值2，否则返回值1</li><li>nullif(值1, 值2) 如果值1与值2相等，那么返回NULL</li><li>isnull(值) 判断值是否为NULL</li></ul><p>比如现在我们想判断：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#81A1C1;"> IF</span><span style="color:#D8DEE9FF;">(</span><span style="color:#B48EAD;">1</span><span style="color:#81A1C1;"> &lt;</span><span style="color:#B48EAD;"> 0</span><span style="color:#D8DEE9FF;">,</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">lbwnb</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">,</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#A3BE8C;">yyds</span><span style="color:#ECEFF4;">&#39;</span><span style="color:#D8DEE9FF;">)</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>通过判断函数，我们就可以很方便地进行一些条件判断操作。</p><p>除了IF条件判断，我们还可以使用类似Switch一样的语句完成多分支结构：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> </span></span>
<span class="line"><span style="color:#81A1C1;">CASE</span><span style="color:#B48EAD;"> 2</span></span>
<span class="line"><span style="color:#81A1C1;">	WHEN</span><span style="color:#B48EAD;"> 1</span><span style="color:#81A1C1;"> THEN</span></span>
<span class="line"><span style="color:#B48EAD;">		10</span></span>
<span class="line"><span style="color:#81A1C1;">	ELSE</span></span>
<span class="line"><span style="color:#B48EAD;">		5</span></span>
<span class="line"><span style="color:#81A1C1;">END</span><span style="color:#D8DEE9FF;">;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>我们也可以将自定义的判断条件放入When之后，它类似于else-if：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> </span></span>
<span class="line"><span style="color:#81A1C1;">CASE</span></span>
<span class="line"><span style="color:#81A1C1;">	WHEN</span><span style="color:#B48EAD;"> 3</span><span style="color:#81A1C1;">&gt;</span><span style="color:#B48EAD;">5</span><span style="color:#81A1C1;"> THEN</span></span>
<span class="line"><span style="color:#B48EAD;">		10</span></span>
<span class="line"><span style="color:#81A1C1;">	WHEN</span><span style="color:#B48EAD;"> 0</span><span style="color:#81A1C1;">&lt;</span><span style="color:#B48EAD;">1</span><span style="color:#81A1C1;"> THEN</span></span>
<span class="line"><span style="color:#B48EAD;">		11</span></span>
<span class="line"><span style="color:#81A1C1;">	ELSE</span></span>
<span class="line"><span style="color:#B48EAD;">		5</span></span>
<span class="line"><span style="color:#81A1C1;">END</span><span style="color:#D8DEE9FF;">;</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>还有一个类似于Java中的Thread.sleep的函数，以秒为单位：</p><div class="language-sql line-numbers-mode" data-highlighter="shiki" data-ext="sql" style="background-color:#2e3440ff;color:#d8dee9ff;"><pre class="shiki nord vp-code"><code class="language-sql"><span class="line"><span style="color:#81A1C1;">SELECT</span><span style="color:#D8DEE9FF;"> sleep(</span><span style="color:#B48EAD;">10</span><span style="color:#D8DEE9FF;">);</span></span></code></pre><div class="line-numbers" aria-hidden="true" style="counter-reset:line-number 0;"><div class="line-number"></div></div></div><p>有关MySQL8.0新增的窗口函数这里暂时不做介绍。</p>`,247)])])}const d=e(o,[["render",p]]),t=JSON.parse('{"path":"/posts/%E5%85%B6%E4%BB%96/3.sjk.html","title":"数据库概览3","lang":"zh-CN","frontmatter":{"title":"数据库概览3","cover":"/assets/images/cover3.jpg","icon":"pen-to-square","date":"2025-12-23T00:00:00.000Z","category":["数据库"],"star":true,"sticky":true,"author":"superxuan","description":"数据库定义语言（DDL） 数据库操作 我们可以通过create database来创建一个数据库： 为了能够支持中文，我们在创建时可以设定编码格式： 如果我们创建错误了，我们可以将此数据库删除，通过使用drop database来删除一个数据库： 创建表 数据库创建完成后，我们一般通过create table语句来创建一张表： SQL数据类型 以下的数...","head":[["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"数据库概览3\\",\\"image\\":[\\"https://img-blog.csdnimg.cn/2019053022120536.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3dlaXhpbl80Mzg1ODIwMQ==,size_16,color_FFFFFF,t_70\\",\\"https://img-blog.csdnimg.cn/20190530221543230.png?x-oss-process=image/watermark,type_ZmFuZ3poZW5naGVpdGk,shadow_10,text_aHR0cHM6Ly9ibG9nLmNzZG4ubmV0L3dlaXhpbl80Mzg1ODIwMQ==,size_16,color_FFFFFF,t_70\\"],\\"datePublished\\":\\"2025-12-23T00:00:00.000Z\\",\\"dateModified\\":\\"2025-12-23T13:13:47.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"superxuan\\"}]}"],["meta",{"property":"og:url","content":"https://mister-hope.github.io/myblog/posts/%E5%85%B6%E4%BB%96/3.sjk.html"}],["meta",{"property":"og:site_name","content":"superxuan的个人博客"}],["meta",{"property":"og:title","content":"数据库概览3"}],["meta",{"property":"og:description","content":"数据库定义语言（DDL） 数据库操作 我们可以通过create database来创建一个数据库： 为了能够支持中文，我们在创建时可以设定编码格式： 如果我们创建错误了，我们可以将此数据库删除，通过使用drop database来删除一个数据库： 创建表 数据库创建完成后，我们一般通过create table语句来创建一张表： SQL数据类型 以下的数..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:image","content":"https://mister-hope.github.io/myblog/assets/images/cover3.jpg"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2025-12-23T13:13:47.000Z"}],["meta",{"name":"twitter:card","content":"summary_large_image"}],["meta",{"name":"twitter:image:src","content":"https://mister-hope.github.io/myblog/assets/images/cover3.jpg"}],["meta",{"name":"twitter:image:alt","content":"数据库概览3"}],["meta",{"property":"article:author","content":"superxuan"}],["meta",{"property":"article:published_time","content":"2025-12-23T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2025-12-23T13:13:47.000Z"}]]},"git":{"createdTime":1766490920000,"updatedTime":1766495627000,"contributors":[{"name":"superxuan05","username":"superxuan05","email":"2758157702@qq.com","commits":2,"url":"https://github.com/superxuan05"}]},"readingTime":{"minutes":20.58,"words":6174},"filePathRelative":"posts/其他/3.sjk.md","excerpt":"<h2>数据库定义语言（DDL）</h2>\\n<h3>数据库操作</h3>\\n<p>我们可以通过<code>create database</code>来创建一个数据库：</p>\\n<div class=\\"language-sql line-numbers-mode\\" data-highlighter=\\"shiki\\" data-ext=\\"sql\\" style=\\"background-color:#2e3440ff;color:#d8dee9ff\\"><pre class=\\"shiki nord vp-code\\"><code class=\\"language-sql\\"><span class=\\"line\\"><span style=\\"color:#81A1C1\\">create</span><span style=\\"color:#81A1C1\\"> database</span><span style=\\"color:#88C0D0\\"> 数据库名</span></span></code></pre>\\n<div class=\\"line-numbers\\" aria-hidden=\\"true\\" style=\\"counter-reset:line-number 0\\"><div class=\\"line-number\\"></div></div></div>","autoDesc":true}');export{d as comp,t as data};
